import { IMAGEURL } from "../../../API/APImovies";
import noPicture from "../../../images/noMovieFoto.png";

import { moviesTypes } from "../../../types";

import styles from "./SiderItem.module.scss";

interface SiderItemProps {
  item: moviesTypes.MoviesItem;
}

const SiderItem: React.FC<SiderItemProps> = ({ item }) => {
  // console.log(item);
  return (
    <li className={styles.container} onClick={() => console.log(item.id)}>
      <img
        className={`${styles.image} `}
        src={`${IMAGEURL}${item?.backdrop_path}`}
        alt={item?.title}
        onError={(e: React.ChangeEvent<HTMLImageElement>): void => {
          e.target.src = noPicture;
        }}
      />
      <div className={styles.containerTitle}>
        <p className={styles.title}>{item.title}</p>
      </div>
    </li>
  );
};

export default SiderItem;
