  //   //   try {
  //   //     // const results = await getMoviesSearch(query!, page);

  //   //     if (results.length < 20) {
  //   //       setEndFix(false);
  //   //     }

  //   //     if (results.length > 0) {
  //   //       setMoviesSearch((prevState) => [...prevState, ...results]);
  //   //       setLoading(false);
  //   //     }

  //   //     if (results.length === 0) {
  //   //       toast.error('Not found');
  //   //       setLoading(true);
  //   //     }
  //   //   } catch (error) {
  //   //     console.log(error);
  //   //   }
  //   // }

  //   // if (pageReff.current === page && prevQuery.current === query) {
  //   //   return;
  //   // }

  //   // if (prevQuery.current !== query) {
  //   //   setMoviesSearch([]);
  //   //   setPage(1);
  //   //   setEndFix(true);
  //   // }

  //   // if (prevQuery.current !== query && page === 1) {
  //   //   pageReff.current = null;
  //   // }

  //   if (query && pageReff.current !== page) {
  //     fetchFilms();
  //   }

  //   prevQuery.current = query;
  //   pageReff.current = page;
  // }, [query, page]);

  const showNextMovies = () => {
    if (movies.length !== 0) {
      setPage(page + 1);
    }
  };

  return (
    <div className={styles.container}>
      {!isLoader && (
        <InfiniteScroll
          dataLength={movies.length}
          next={showNextMovies}
          hasMore={endFix}
          scrollThreshold={1}
          loader={<Loader size={70} />}
        >
          {/* <ul className={styles.list}>
            {moviesSearch.map((item) => (
              <MoviesItem
                key={`${item.id}_${nanoid()}`}
                data={item}
                genres={genres}
              />
            ))}
          </ul> */}
        </InfiniteScroll>
      )}
      {isLoader && <NotFound />}
      <ScrollToTop />
    </div>
  );
};

export default Movies;
