import axios from 'axios';

import { moviesTypes } from '../types';

// export const BASEURL = 'https://api.themoviedb.org/3';
export const BASEURL = 'http://www.omdbapi.com/?i=tt3896198&';
export const IMAGEURL = 'https://image.tmdb.org/t/p/w780/';

const API_KEY = '?apikey=7e8c7aae';

export const movies = axios.create({
  baseURL: `${BASEURL}`,
});

async function getMoviesRequest(): Promise<
// page: number
  moviesTypes.MoviesItem[] | undefined
> {
  try {
    let response = await movies.get(`${API_KEY}&page=${1}`);
    return response.data.results;
  } catch (error) {
    console.log(error);
    if (axios.isCancel(error)) {
      return Promise.reject();
    } else {
      console.log('Error', error);
      return;
    }
  }
}
async function getMoviesGenre(): Promise<moviesTypes.GenresType[] | undefined> {
  try {
    let response = await movies.get(
      `/genre/movie/list${API_KEY}&language=en-US`
    );
    return response.data.genres;
  } catch (error) {
    console.log(error);
    if (axios.isCancel(error)) {
      return Promise.reject();
    } else {
      console.log('Error', error);
      return;
    }
  }
}
async function getMovie(
  id: string
): Promise<moviesTypes.OneMovieType | undefined> {
  try {
    let response = await movies.get(`/movie/${id}${API_KEY}&language=en-US`);
    return response.data;
  } catch (error) {
    console.log(error);
    if (axios.isCancel(error)) {
      return Promise.reject();
    } else {
      console.log('Error', error);
      return;
    }
  }
}

async function getCredits(
  id: string
): Promise<moviesTypes.CreditsType | undefined> {
  try {
    let response = await movies.get(
      `/movie/${id}/credits${API_KEY}&language=en-US`
    );
    return response.data;
  } catch (error) {
    console.log(error);
    if (axios.isCancel(error)) {
      return Promise.reject();
    } else {
      console.log('Error', error);
      return;
    }
  }
}
async function getImages(
  id: string
): Promise<moviesTypes.ImagesType | undefined> {
  try {
    let response = await movies.get(`/movie/${id}/images${API_KEY}`);
    return response.data;
  } catch (error) {
    console.log(error);
    if (axios.isCancel(error)) {
      return Promise.reject();
    } else {
      console.log('Error', error);
      return;
    }
  }
}

async function getReviews(
  id: string
): Promise<moviesTypes.ReviewsType[] | undefined> {
  try {
    let response = await movies.get(
      `/movie/${id}/reviews${API_KEY}&language=en-US`
    );
    return response.data.results;
  } catch (error) {
    console.log(error);
    if (axios.isCancel(error)) {
      return Promise.reject();
    } else {
      console.log('Error', error);
      return;
    }
  }
}

async function getMoviesSearch(
  search: string | null,
  page: number
): Promise<any> {
  try {
    let response = await movies.get(
      `/search/movie${API_KEY}&query=${search}&language=en-US&page=${page}`
    );
    return response.data.results;
  } catch (error) {
    console.log(error);
    if (axios.isCancel(error)) {
      return Promise.reject();
    } else {
      console.log('Error', error);
      return;
    }
  }
}

export {
  getMoviesRequest,
  getMoviesGenre,
  getMovie,
  getCredits,
  getImages,
  getReviews,
  getMoviesSearch,
};
