import { useEffect, useRef } from "react";

import { useAppSelector, useAppDispatch } from "../../redux/hook";
import { operations, selectors } from "../../redux/movies";

import { Slider } from "../";

import styles from "./Home.module.scss";

const Home: React.FC = () => {
  const moviesList = useAppSelector(selectors.getMovies);
  const dispatch = useAppDispatch();
  const pageLoaded = useRef<number | null>(null);

  const topMovies = moviesList.slice(0, 8);

  useEffect(() => {
    if (1 === pageLoaded.current) {
      return;
    }
    pageLoaded.current = 1;

    dispatch(operations.fetchMovies(1));
  }, [dispatch]);

  return (
    <div className={styles.container}>
      <Slider topMovies={topMovies} />
    </div>
  );
};

export default Home;
