import { combineReducers } from 'redux';
import { createReducer } from '@reduxjs/toolkit';
import 'react-toastify/dist/ReactToastify.css';

import {
  fetchMovies,
  // fetchOneMovie,
} from './operations';
import { moviesAction } from './action';

type MovieState = {
  data: any[];
  totalResults: number | null;
  error: boolean;
};

// type MovieManualState = {
//   movie: null | any;
//   credits: any[];
//   posters: any[];
//   backImg: any[];
//   reviews: any[];
//   loading: boolean;
// };

const initialState: MovieState = {
  data: [],
  totalResults: null,
  error: false,
};

const moviesSearchReducer = createReducer(initialState, (builder) => {
  builder
    .addCase(fetchMovies.fulfilled, (state, { payload }) => {
      state.data = [...state.data, ...payload?.Search];
      state.totalResults = Number(payload?.totalResults);

      if (payload.Error) {
        state.error = true;
        return;
      }
      state.error = false;
    })
    .addCase(moviesAction, (state, { payload }) => {
      state.data = payload;
      state.error = false;
      state.totalResults = null;
    });
});

