import { createAsyncThunk } from '@reduxjs/toolkit';
import { getMoviesSearchRequest } from '../../API/APImovies';

// import { moviesTypes } from '../../types';

const fetchMovies = createAsyncThunk<any, {}, { rejectValue: any }>(
  'movies/fetchMovies',
  async (data, { rejectWithValue }: any) => {
    try {
      const response = await getMoviesSearchRequest(page);
      return response;
    } catch (error) {
      return rejectWithValue(error);
    }
  }
);

