import { Swiper, SwiperSlide } from "swiper/react";

import { EffectCoverflow, Pagination } from "swiper";
import { ButtonNext, ButtonPrev } from "./swiperButton";

import { SiderItem } from "..";

import popcorn from "../../images/popcorn.png";

import { moviesTypes } from "../../types";

import styles from "./Slider.module.scss";

interface SliderProps {
  topMovies: moviesTypes.MoviesItem[];
}

const Slider: React.FC<SliderProps> = ({ topMovies }) => {
  return (
    <div className={styles.container}>
      <div className={styles.containerTitle}>
        <h1 className={styles.title}>Top movies today</h1>
      </div>
      <img className={styles.popcorn} src={popcorn} alt="popcorn" />
      <Swiper
        effect={"coverflow"}
        // grabCursor={true}
        centeredSlides={true}
        slidesPerView={"auto"}
        coverflowEffect={{
          rotate: 50,
          stretch: 0,
          depth: 200,
          modifier: 1,
          slideShadows: false,
        }}
        pagination={{ clickable: true }}
        modules={[EffectCoverflow, Pagination]}
        // onSlideChange={() => console.log("slide change")}
        // onSwiper={(swiper) => console.log(swiper)}
        className="mySwiper"
      >
        {topMovies.map((item) => (
          <SwiperSlide key={item.id}>
            <SiderItem item={item} />
          </SwiperSlide>
        ))}
        <div className={styles.containerButton}>
          <ButtonPrev />
          <ButtonNext />
        </div>
      </Swiper>
    </div>
  );
};

export default Slider;
